package aggregator;import java.io.IOException;import java.io.UnsupportedEncodingException;import java.net.URLEncoder;import java.util.ArrayList;import java.util.List;import org.jsoup.Jsoup;import org.jsoup.nodes.Document;import org.jsoup.nodes.Element;import org.jsoup.select.Elements;import aggregator.IAggregator;import aggregator.Link.Builder;/** * Results of Google * @author Narasimman * */public class GoogleSearchAggregator implements IAggregator {  private static final String BASE_URL = "http://www.google.com/search?q=";  @Override  public List<Link> getResults(String query) {    Document doc;    List<Link> results = new ArrayList<Link>();    try {      String queryEncoded = BASE_URL + URLEncoder.encode(query, "UTF-8");      doc = Jsoup.connect(queryEncoded)          .userAgent("Mozilla")          .ignoreHttpErrors(true)          .timeout(5000)          .get();      Elements linkList = doc.select("div.g");      for (Element element : linkList) {        Elements result = element.select("h3.r a");        final String title = result.text();        final String url = result.attr("href");        Link link = new Builder()        .url(url)        .title(title)        .type(SourceType.GOOGLE)        .build();        results.add(link);      }    } catch (UnsupportedEncodingException e) {            e.printStackTrace();    } catch (IOException e) {      e.printStackTrace();    }    return results;  }}