package aggregator;/** * Class of the search engine results fetched * @author Narasimman * */public class Link {    private final String url;  private final String title;  private final SourceType type;  private Link(Builder builder) {    this.url = builder.url;    this.title = builder.title;    this.type = builder.type;  }  /**   * Builders to create link objects (Builder Pattern)   * @author Narasimman   *   */  public static class Builder {    private String url;    private String title;    private SourceType type;    public Builder url(String url) {      this.url = url;      return this;    }    public Builder title(String title) {      this.title = title;      return this;    }    public Builder type(SourceType type) {      this.type = type;      return this;    }    public Link build() {      return new Link(this);    }  }  public String getUrl() {    return url;  }  public String getTitle() {    return title;  }  public SourceType getType() {    return type;  }}