package ui;import java.util.ArrayList;import java.util.List;import aggregator.BingSearchAggregator;import aggregator.GoogleSearchAggregator;import aggregator.Link;import aggregator.IAggregator;import aggregator.YahooSearchAggregator;import com.google.gson.Gson;/** * The aggregator that calls different search engine requestors and * accumulates the result * @author Narasimman * */public class SearchAggregator {  public static String search(String query) {    List<Link> resultsList = new ArrayList<Link>();    IAggregator gs = new GoogleSearchAggregator();    resultsList.addAll(gs.getResults(query));    IAggregator ys = new YahooSearchAggregator();    resultsList.addAll(ys.getResults(query));    IAggregator bs = new BingSearchAggregator();    resultsList.addAll(bs.getResults(query));    String results = new Gson().toJson(resultsList);    return results;  }  /**   * @param args   */  public static void main(String[] args) {    System.out.println(search("hashmap"));  }}