package ui;import java.io.IOException;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;/** * Servlet implementation class SearchServlet */@WebServlet("/search")public class SearchServlet extends HttpServlet {  private static final long serialVersionUID = 1L;    public SearchServlet() {    super();  }  protected void doGet(HttpServletRequest request, HttpServletResponse response)      throws ServletException, IOException {    try {      String query = request.getParameter("q");                  String results = "";      if(!query.isEmpty()) {        results = SearchAggregator.search(query);      }        response.setContentType("text/json; charset=UTF-8");            response.setContentLength(results.length());      response.getOutputStream().write(results.getBytes());      response.getOutputStream().flush();      response.getOutputStream().close();    } catch (Exception e) {      e.printStackTrace();    }  }  protected void doPost(HttpServletRequest request, HttpServletResponse response)      throws ServletException, IOException {    doGet(request, response);  }}